$hookUrl = 'https://discord.com/api/webhooks/1049867909893202000/1M32d9XAmXo0c9QsAEVTtp4zuyMLc9V5Q5Fp4WWFZaMG4kdiJPJQUkhm6Yf6tGLU1nVE'

